    //       <li key={photo.id}>
    //         <Link href={`/photo?id=${photo.urls.small}`} as="photo">
    //           <img src={photo.urls.small} width="500" height="300"></img>
    //         </Link>
    //       </li>
    //     ))}
    //   </ul>
    // </InfiniteScroll>
    <div>
      <h1>demo: react-infinite-scroll-component</h1>
      <hr />
      <InfiniteScroll
        dataLength={photos.length}
        next={fetchMoreData}
        hasMore={true}
        loader={<h4>Loading...</h4>}
      >
        <ul>
          {photos.map((photo) => (
            <li key={photo.id}>
              <Link href={`/photo?id=${photo.urls.small}`} as="photo">
                <img src={photo.urls.small} width="500" height="300"></img>
              </Link>
            </li>
          ))}
        </ul>
      </InfiniteScroll>
    </div>
  );
};
Index.getInitialProps = async (ctx) => {
  const res = await axios.get(
    "https://api.unsplash.com/photos/?client_id=oPeX2b24aAVWiEmjKq0I2RRr6XbF_KpwUutWmFufaGU&per_page=400"
  );
  const { data } = res;
  return { photos: data };
};
export default Index;
